import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({ title: '', description: '', completed: false });
  const [editingId, setEditingId] = useState(null);
  const token = localStorage.getItem('token');

  const fetchTasks = async () => {
    const res = await axios.get('http://localhost:5001/items', {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(res.data);
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editingId) {
      await axios.put(`http://localhost:5001/items/${editingId}`, form, {
        headers: { Authorization: `Bearer ${token}` },
      });
    } else {
      await axios.post('http://localhost:5001/items', form, {
        headers: { Authorization: `Bearer ${token}` },
      });
    }
    setForm({ title: '', description: '', completed: false });
    setEditingId(null);
    fetchTasks();
  };

  const handleEdit = (task) => {
    setForm({ title: task.title, description: task.description, completed: task.completed });
    setEditingId(task.id);
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5001/items/${id}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    fetchTasks();
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div className="min-h-screen p-10 bg-gray-100">
      <div className="flex justify-between mb-6">
        <h1 className="text-2xl font-bold">Task Manager</h1>
        <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded">
          Logout
        </button>
      </div>

      <form onSubmit={handleSubmit} className="bg-white p-4 rounded shadow mb-6">
        <input
          type="text"
          placeholder="Title"
          className="mr-2 p-2 border rounded w-1/4"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          className="mr-2 p-2 border rounded w-1/2"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
        />
        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          {editingId ? 'Update' : 'Add'}
        </button>
      </form>

      <div className="space-y-4">
        {tasks.map((task) => (
          <div key={task.id} className="bg-white p-4 rounded shadow flex justify-between items-center">
            <div>
              <h3 className="font-semibold">{task.title}</h3>
              <p className="text-sm text-gray-600">{task.description}</p>
            </div>
            <div className="space-x-2">
              <button onClick={() => handleEdit(task)} className="bg-yellow-400 text-white px-3 py-1 rounded">
                Edit
              </button>
              <button onClick={() => handleDelete(task.id)} className="bg-red-500 text-white px-3 py-1 rounded">
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
